    * - it displays errors if needed or success and fixes local cached data for
    * the user;
    * - it also updates UI lang if needed;
    * - finally, it authenticates new created user.
    */

    c.submit.subscribe = function (e) {
      e.preventDefault();
      if (c.data.password() !== c.data.passwordConfirm()) {
        notif.warning({ body: conf.LANG.USER.ERR.PASSWORD_MISMATCH });
        document.querySelector('input[name="passwordConfirm"]').focus();
      } else if (hasSpecialCharacter([c.data.login(), c.data.firstname(), c.data.lastname(), c.data.padNickname()]) === true) {
        notif.warning({ body: conf.LANG.USER.ERR.REQUIRED_ALPHANUMERIC });
      } else {
        c.data.login(c.data.login().trim());
        m.request({
          method: 'POST',
          url: conf.URLS.USER,
          data: c.data
        }).then(function (resp) {
          var msg = resp.value.active ? conf.LANG.USER.AUTH.SUBSCRIBE_SUCCESS : conf.LANG.USER.AUTH.SUBSCRIBE_CONFIRM_SUCCESS;
          m.route('/login');
