    c.removeAccount = function () {
      var password = window.prompt(conf.LANG.USER.INFO.REMOVE_ACCOUNT_SURE);
      if (password) {
        var login = auth.userInfo().login;
        m.request({
          method: 'POST',
          url: conf.URLS.CHECK,
          data: {
            login: login,
            password: password,
            auth_token: auth.token()
          }
        }).then(function () {
          m.request({
            method: 'DELETE',
